import React, { useState, useRef } from 'react';
import useOnScreen from '../hooks/useOnScreen';
import './../styles/YouTube.css';
import channelAvatar from './../assets/images/ProfilePic.png';

const youtubeVideos = [
  {
    title: 'Ra.Ramkumar | National Awardee | PARKING | Interview',
    id: 'ugJ7TETZnn0',
    channelName: 'Channel 1',
  },
  {
    title: 'Parking Movie Team Interview | Harish Kalyan, MS Bhaskar, Ramkumar | Indhuja | Yuvan Shankar Raja',
    id: 'su5DLWyP4Sw',
    channelName: 'Channel 2',
  },
  // Add more videos here
];

const YouTube = () => {
  const [currentVideo, setCurrentVideo] = useState(youtubeVideos[0]);
  const ref = useRef();
  const isVisible = useOnScreen(ref, '-100px');

  const getEmbedUrl = (videoId) => {
    return `https://www.youtube.com/embed/${videoId}`;
  };

  return (
    <div ref={ref} className={`youtube-container ${isVisible ? 'visible' : ''}`}>
      <h2 className="youtube-title">My Videos</h2>
      <div className="main-video">
        <iframe
          width="100%"
          height="100%"
          src={getEmbedUrl(currentVideo.id)}
          title={currentVideo.title}
          frameBorder="0"
          allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
          allowFullScreen
        ></iframe>
      </div>
      <div className="video-list">
        {youtubeVideos.map((video) => (
          <div
            key={video.id}
            className={`video-list-item ${currentVideo.id === video.id ? 'active' : ''}`}
          >
            <img src={channelAvatar} alt="Channel Avatar" className="channel-avatar" />
            <div className="video-details">
              <p className="video-title-small">{video.title}</p>
              <div className="video-actions">
                <button onClick={() => setCurrentVideo(video)}>Play</button>
                <a href={`https://www.youtube.com/watch?v=${video.id}`} target="_blank" rel="noopener noreferrer">
                  More
                </a>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default YouTube;
