  // Add more videos here
];

const video1='ugJ7TETZnn0';
const video2='su5DLWyP4Sw';

const YouTube = () => {
  const getEmbedUrl = (videoId) => {
    return `https://www.youtube.com/embed/${videoId}`;
  };

  return (
    <div className="youtube-container">
      <h2 className="youtube-title">Work</h2>
  <div style={{ display: 'flex', flexDirection: isMobile ? 'column' : 'row', gap: isMobile ? '20px' : '100px', alignItems: 'center' }}>
    <div className={isMobile ? "video-card" : "video-card1"}>
      <iframe
              width="100%"
              height={isMobile ? "200" : "350"}
              src={getEmbedUrl(video1)}
              frameBorder="0"
              allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
              allowFullScreen
            >
       </iframe>
       </div>
       <div className={isMobile ? "video-card" : "video-card1"}>
      <iframe
              width="100%"
              height={isMobile ? "200" : "350"}
              src={getEmbedUrl(video2)}
              frameBorder="0"
              allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
              allowFullScreen
            >
       </iframe>
       </div>
       </div>
             <h3 className="youtube-title">Gallery</h3>

      <div className="video-grid">
        {youtubeVideos.map((video) => (
          <div key={video.id} className="video-card">
            <iframe
              width="100%"
              height="200"
              src={getEmbedUrl(video.id)}
              title={video.title}
              frameBorder="0"
              allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
              allowFullScreen
            ></iframe>
          </div>
        ))}
      </div>
    </div>
  );
};

export default YouTube;
