import React, { useEffect, useRef, useState } from 'react';

export default function NatRotator({ items = [], interval = 5000 }) {
    const [index, setIndex] = useState(0);
    const timerRef = useRef(null);

    useEffect(() => {
        if (!items.length) return;
        clearTimeout(timerRef.current);
        timerRef.current = setTimeout(() => {
            setIndex((i) => (i + 1) % items.length);
        }, interval);
        return () => clearTimeout(timerRef.current);
    }, [index, items, interval]);

    const current = items[index];
    if (!current) return null;

    return (
        <div className="nat-rotator" aria-hidden={false}>
            {current.type === 'video' ? (
                <>
                    <video
                        key={current.src + index}
                        className="nat-item"
                        src={current.src}
                        autoPlay
                        muted
                        playsInline
                        onEnded={() => setIndex((i) => (i + 1) % items.length)}
                    />
                    {current.title && (
                        <div className="video-overlay">
                            {current.title}
                        </div>
                    )}
                </>
            ) : (
                <img key={current.src + index} className="nat-item" src={current.src} alt="" />
            )}
        </div>
    );
}
