import { useState, useEffect, useRef } from 'react';

const useTitleAnimation = (title) => {
    const [letterColors, setLetterColors] = useState(Array(title.length).fill('#FFFFFF'));
    const blue = '#0000FF';
    const white = '#FFFFFF';

    const intervalRef = useRef(null);
    const timeoutRef = useRef(null);
    const currentIndexRef = useRef(0); // Single source of truth

    useEffect(() => {
        const startAnimation = () => {
            intervalRef.current = setInterval(() => {
                setLetterColors(currentColors => {
                    const newColors = [...currentColors];
                    newColors[currentIndexRef.current] = blue;
                    return newColors;
                });

                currentIndexRef.current++; // Always use ref

                if (currentIndexRef.current === title.length) {
                    clearInterval(intervalRef.current);

                    timeoutRef.current = setTimeout(() => {
                        currentIndexRef.current = 0; // Fix: Use ref, not const
                        startAnimation();
                    }, 10000);
                }
            }, 2000);
        };

        startAnimation();

        return () => {
            clearInterval(intervalRef.current);
            clearTimeout(timeoutRef.current);
        };
    }, [title, blue]);

    return letterColors;
};

export default useTitleAnimation;
