import { useState, useEffect } from 'react';

const useTitleAnimation = (title) => {
    const [animatedTitle, setAnimatedTitle] = useState({ text: '', color: '#FFFFFF' });
    const colors = ["#FFFFFF", "#ADD8E6", "#87CEEB", "#6495ED", "#4169E1", "#0000FF", "#0000CD", "#00008B", "#000080", "#191970"];

    useEffect(() => {
        let currentIndex = 0;
        let colorIndex = 0;

        const intervalId = setInterval(() => {
            if (currentIndex <= title.length) {
                setAnimatedTitle(prev => ({
                    ...prev,
                    text: title.substring(0, currentIndex)
                }));
                currentIndex++;
            } else {
                setAnimatedTitle(prev => ({
                    ...prev,
                    color: colors[colorIndex % colors.length]
                }));
                colorIndex++;
            }
        }, 200); // Adjust speed of animation

        return () => clearInterval(intervalId);
    }, [title]);

    return animatedTitle;
};

export default useTitleAnimation;
