import React, { useEffect, useRef, useState } from 'react';

export default function NatRotator({ items = [], intervalVd = 28000, intervalImg = 8000 }) {
    const [index, setIndex] = useState(0);
    const [isTransitioning, setIsTransitioning] = useState(false);
    const timerRef = useRef(null);
    
    const current = items[index];

    useEffect(() => {
        if (!items.length) return;
        
        // Start exit transition
        setIsTransitioning(true);
        setTimeout(() => {
            setIndex((i) => (i + 1) % items.length);
            setIsTransitioning(false);
        }, 400); // Half transition duration
        
        // Auto timer
        clearTimeout(timerRef.current);
        const interval = current?.type === "video" ? intervalVd : intervalImg;
        timerRef.current = setTimeout(() => {
            setIsTransitioning(true);
            setTimeout(() => setIndex((i) => (i + 1) % items.length), 400);
        }, interval - 400);
        
        return () => clearTimeout(timerRef.current);
    }, [index, items, intervalVd, intervalImg]);

    if (!current) return null;

    return (
        <div className="nat-rotator" aria-hidden={false}>
            <div 
                key={current.src + index}
                className={`nat-item ${isTransitioning ? 'exiting' : 'active'}`}
            >
                {current.type === 'video' ? (
                    <video
                        src={current.src}
                        autoPlay muted playsInline
                        onEnded={() => setIndex((i) => (i + 1) % items.length)}
                    />
                ) : (
                    <img src={current.src} alt="" />
                )}
            </div>
        </div>
    );
}
