import { useEffect } from 'react';

const useTitleAnimation = () => {
    useEffect(() => {
        const title = "Ramkumar Balakrishnan";
        let colorIndex = 0;
        const colors = ["#FFFFFF", "#0000FF"]; // White to Blue

        const changeTitle = () => {
            document.title = title;
            document.querySelector('title').style.color = colors[colorIndex];
            colorIndex = (colorIndex + 1) % colors.length;
        };

        const intervalId = setInterval(changeTitle, 1000); // Change every second

        return () => clearInterval(intervalId); // Cleanup on unmount
    }, []);
};

export default useTitleAnimation;
